<script>
export default {
    data() {
    return {
      opacity: 0,
      
    };
  },
     computed: {
    Fade() {
      return `opacity: ${this.opacity};`
    },
  },
  methods: {
    scrollfade() {
        if(window.pageYOffset<500){
            this.opacity =  window.pageYOffset/200
           
            if(this.opacity >1){
                this.opacity = 1
            }
        } 
        if(window.pageYOffset>750 && window.pageYOffset<1400){
            this.opacity = 1/(window.pageYOffset/(1500-window.pageYOffset))
        }
        if(window.pageYOffset>1400){
             this.opacity =  this.opacity*1;
        }
      
      //this.top= 1 - window.pageYOffset
    }
  },
    mounted(){
      document.addEventListener("scroll", this.scrollfade);
    }
}
</script>

